export type UserCredentials = {
  /**
   * The username of the user
   */
  username: string

  /**
   * The password of the user
   */
  password: string
}

export type UserDetails = {
  /**
   * The first name of the user
   */
  firstName: string

  /**
   * The last name of the user
   */
  lastName: string

  /**
   * The postal code of the user
   */
  postalCode: string
}
